INSERT INTO `xiaobing360_category` VALUES ('17', '', '品牌网站', '澳大利亚商业移民', '1', '1', '4', '10', '', '', '', '', '', '', 'page_tongyong', '', '5', '2,3', '4', '0', '1', '1', '0', '0', '', '', '1465196247', '1495590893', '1', '0', '154', '0', '', '<p style=\"text-align:center;margin-left:0in;\">\r\n	<span class=\"title\" style=\"font-size:4em;color:#FF6C00;line-height:1;font-family:微软雅黑, Arial;\">品牌型网站</span><span class=\"brief\" style=\"font-size:2em;color:#FF6C00;line-height:2;font-family:微软雅黑, Arial;\">塑造品牌形象 传递品牌价值</span> \r\n</p>\r\n<div class=\"p\" style=\"padding:0px;margin:10px 0px 0px;font-size:1.167em;color:#555555;font-family:微软雅黑, Arial;\">\r\n	<p>\r\n		品牌型网站着重展示企业文化和品牌特色，非常强调创意设计与文化的结合，能通过视觉感官的冲击，令目标受众对品牌产生深刻而美好的印象。万户网络品牌型网站建设利用多媒体交互技术、动态网页技术，配合广告设计，将企业品牌形象在互联网上多维度、全方位、立体式地表现出来。对于产品品牌众多的企业，可单独建立各品牌的独立网站，以方便市场营销策略与网站宣传的统一。\r\n	</p>\r\n</div>\r\n<p>\r\n	<br />\r\n</p>\r\n<p style=\"text-align:center;margin-left:0in;\">\r\n	<br />\r\n</p>', '', '11', '1');
INSERT INTO `xiaobing360_category` VALUES ('18', '', '互动营销', '加拿大魁省投资移民', '1', '1', '3', '10', '', '', '', '', '', '', 'page_tongyong', '', '5', '2,3', '4', '0', '1', '1', '0', '0', '', '', '1468549478', '1495590889', '1', '0', '92', '0', '', '<p style=\"text-align:center;margin-left:0in;\">\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:18px;\"><strong><span class=\"title\" style=\"font-size:4em;color:#FF6C00;line-height:1;font-family:微软雅黑, Arial;\">互动营销</span><span class=\"brief\" style=\"font-size:2em;color:#FF6C00;line-height:2;font-family:微软雅黑, Arial;\">高效互动 提升业绩</span> </strong></span>\r\n</p>\r\n<div class=\"p\" style=\"padding:0px;margin:10px 0px 0px;font-size:1.167em;color:#FFFFFF;font-family:微软雅黑, Arial;\">\r\n	<strong><span style=\"color:#333333;\">互动营销型网站的特点在于能加强与客户之间的交流，利用互联网(尤其是移动互联网)的特性，提升客户管理的准确性、高效性，从而打造品牌、提升销售业绩。万户网络互动营销网站能精准把握企业与消费者的共同利益点，找到巧妙的沟通时机和方法将二者紧密结合起来。对于企业商务活动，网站则可实现渠道分销、终端客户销售、合作伙伴管理、网上采购、实时在线服务</span>、物流管理、售后服务管理等。</strong>\r\n</div>\r\n<p>\r\n	<br />\r\n</p>\r\n<p style=\"font-size:1.167em;color:#FFFFFF;\">\r\n	<span style=\"color:#333333;\"></span> \r\n</p>\r\n<p style=\"font-size:1.167em;color:#FFFFFF;\">\r\n	<span style=\"color:#333333;\"><br />\r\n</span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p style=\"text-align:center;margin-left:0in;\">\r\n	<br />\r\n</p>', '', '11', '1');
