INSERT INTO `xiaobing360_category` VALUES ('60', '', '活动策划', '', '3', '3', '6', '10', '', '', '', '', '', '', 'page_tongyong', '', '5', '2,3,7', '4', '0', '1', '1', '0', '0', '', '', '1473303649', '1494915434', '1', '0', '0', '0', '', '<p style=\"margin-left:0in;color:#535353;font-family:宋体;font-size:14px;text-align:center;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:16px;\"><strong><span style=\"font-size:18px;\">匈牙利投资移民</span></strong><strong><span style=\"font-size:18px;\"></span></strong></span>\r\n</p>\r\n<p style=\"margin-left:0in;color:#535353;font-family:宋体;font-size:14px;text-align:center;background-color:#FFFFFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"margin-left:0in;color:#535353;font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp;匈牙利是一个位于欧洲中部的内陆国家，与奥地利、斯洛伐克、乌克兰、罗马尼亚、塞尔维亚、克罗地亚和斯洛文尼亚接壤。匈牙利政府自2013年4月其开通国家特别债券投资项目。申请人通过投资移民匈牙利国家特别债券的方式，可获得匈牙利永居身份。匈牙利项目是不用通过语言考试和履行其他附加条件，仅半年之后就可以获得永居居留卡的申根国移民项目，且可实现一人申请、三代移民的目的。</span>\r\n</p>\r\n<p style=\"margin-left:0in;color:#535353;font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:16px;\"><br />\r\n</span>\r\n</p>\r\n<p style=\"margin-left:0in;color:#535353;font-family:宋体;font-size:14px;text-align:center;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:16px;\"><img src=\"/Websites/zhongyu/Uploads/Editor/2016-07-22/5791d6d32e781.jpg\" alt=\"\" /><br />\r\n</span>\r\n</p>\r\n<p style=\"margin-left:0in;color:#535353;font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:16px;\"><br />\r\n</span>\r\n</p>\r\n<p style=\"margin-left:0in;color:#535353;font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:16px;color:#E56600;\"><strong>一、申请条件</strong></span>\r\n</p>\r\n<p style=\"margin-left:0in;color:#535353;font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp;</span><span style=\"font-family:\'Microsoft YaHei\';font-size:16px;\">1、主申请人年满18周岁；</span>\r\n</p>\r\n<p style=\"margin-left:0in;color:#535353;font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp;2、无犯罪记录；</span>\r\n</p>\r\n<p style=\"margin-left:0in;color:#535353;font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp;3、购买30万欧元匈牙利国家特别债券，5年到期后全额返还。</span>\r\n</p>\r\n<p style=\"margin-left:0in;color:#535353;font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	<span><span style=\"font-size:16px;line-height:24px;\"><b><br />\r\n</b></span></span>\r\n</p>\r\n<p style=\"margin-left:0in;color:#535353;font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:16px;color:#E56600;\"><strong>二、投资额度</strong></span>\r\n</p>\r\n<p style=\"margin-left:0in;color:#535353;font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp;1、全款模式：投资30万欧元至匈牙利特别国债基金用于认购匈牙利国债，投资期5年；</span>\r\n</p>\r\n<p style=\"margin-left:0in;color:#535353;font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp;2、融资模式：投资13.5万欧元或15万欧元至匈牙利特别国债基金用于认购匈牙利国债，投资期5年。</span>\r\n</p>\r\n<p style=\"margin-left:0in;color:#535353;font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:16px;\"><br />\r\n</span>\r\n</p>\r\n<p style=\"margin-left:0in;color:#535353;font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:16px;color:#E56600;\"><strong>三、项目优势</strong></span>\r\n</p>\r\n<p style=\"margin-left:0in;color:#535353;font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:16px;\"> &nbsp; &nbsp; &nbsp; 1、无移民监；</span>\r\n</p>\r\n<p style=\"margin-left:0in;color:#535353;font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:16px;\"> &nbsp; &nbsp; &nbsp; 2、审理速度快，6个月内即可获得永久居留权；</span>\r\n</p>\r\n<p style=\"margin-left:0in;color:#535353;font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp;3、申请材料简单，无需资产来源证明，无需体检，无需纳税证明；</span>\r\n</p>\r\n<p style=\"margin-left:0in;color:#535353;font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:16px;\"> &nbsp; &nbsp; &nbsp; 4、不用登陆匈牙利，可授权律师在国内完成移民手续；</span>\r\n</p>\r\n<p style=\"margin-left:0in;color:#535353;font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:16px;\"> &nbsp; &nbsp; &nbsp; 5、子女可免费享受匈牙利本国人同等的教育、医疗等福利；</span>\r\n</p>\r\n<p style=\"margin-left:0in;color:#535353;font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp;6、匈牙利是申根国家之一，取得居留证后可自由往来各申根国；</span>\r\n</p>\r\n<p style=\"margin-left:0in;color:#535353;font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp;7、匈牙利是欧盟成员国，货物、资金等可在成员国间自由流通；</span>\r\n</p>\r\n<p style=\"margin-left:0in;color:#535353;font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:16px;\"> &nbsp; &nbsp; &nbsp; 8、匈牙利生活成本相对其他欧洲国家较低；</span>\r\n</p>\r\n<p style=\"margin-left:0in;color:#535353;font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp;9、布达佩斯是“世界第一温泉”之都，医生可处方病人浸泡温泉，并被列入当地的医疗保险覆盖范畴内；</span>\r\n</p>\r\n<p style=\"margin-left:0in;color:#535353;font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:16px;\">&nbsp; &nbsp; &nbsp; 10、理想的经商位置，是通往中东欧和欧盟市场的门户，地处于欧洲心脏地带，可同时辐射欧盟成员国及非欧盟成员国市场；</span>\r\n</p>\r\n<p style=\"margin-left:0in;color:#535353;font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:16px;\">&nbsp; &nbsp; &nbsp; 11、拥有总人数达4万的中东欧地区最大华人群体，匈牙利人民对中国移民持有友好欢迎态度。</span>\r\n</p>\r\n<p style=\"margin-left:0in;color:#535353;font-family:宋体;font-size:14px;text-align:center;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:16px;\"><img src=\"/Websites/zhongyu/Uploads/Editor/2016-07-22/5791d6e76912b.jpg\" alt=\"\" /></span>\r\n</p>', '', '11', '1');
