INSERT INTO `xiaobing360_document_article` VALUES ('82', '0', '<div class=\"__kindeditor_paste__\">\r\n	<p style=\"text-align:center;\">\r\n		<img src=\"/Websites/zhongyu/Uploads/Editor/2016-08-08/57a85da94a88c.png\" alt=\"\" /><img src=\"/Websites/zhongyu/Uploads/Editor/2016-08-08/57a85d8542740.png\" alt=\"\" /><img src=\"/Websites/zhongyu/Uploads/Editor/2016-08-08/57a85d3e05e01.png\" alt=\"\" />\r\n	</p>\r\n	<p style=\"text-align:center;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"text-align:center;\">\r\n		<img src=\"/Websites/zhongyu/Uploads/Editor/2016-08-01/579ee8dc27972.jpg\" alt=\"\" />\r\n	</p>\r\n	<p style=\"text-align:center;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"text-align:center;\">\r\n		<img src=\"/Websites/zhongyu/Uploads/Editor/2016-08-02/57a04277cc98c.png\" alt=\"\" />\r\n	</p>\r\n	<p style=\"text-align:center;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"text-align:center;\">\r\n		<img src=\"/Websites/zhongyu/Uploads/Editor/2016-08-02/57a042e44c305.jpg\" alt=\"\" />\r\n	</p>\r\n	<p style=\"text-align:center;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"text-align:center;\">\r\n		<img src=\"/Websites/zhongyu/Uploads/Editor/2016-08-02/57a056071252f.jpg\" alt=\"\" />\r\n	</p>\r\n	<p style=\"text-align:center;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"text-align:center;\">\r\n		<img src=\"/Websites/zhongyu/Uploads/Editor/2016-08-02/57a05c3d93b26.jpg\" alt=\"\" />\r\n	</p>\r\n	<p style=\"text-align:center;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"text-align:center;\">\r\n		<img src=\"/Websites/zhongyu/Uploads/Editor/2016-08-02/57a05f3e17d77.jpg\" alt=\"\" />\r\n	</p>\r\n	<p style=\"text-align:center;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"text-align:center;\">\r\n		<img src=\"/Websites/zhongyu/Uploads/Editor/2016-08-02/57a06a711efdc.jpg\" alt=\"\" />\r\n	</p>\r\n	<p style=\"text-align:center;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"text-align:center;\">\r\n		<img src=\"/Websites/zhongyu/Uploads/Editor/2016-08-02/57a06f6a8305b.jpg\" alt=\"\" />\r\n	</p>\r\n	<p style=\"text-align:center;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"text-align:center;\">\r\n		<br />\r\n	</p>\r\n	<div style=\"padding:0px;margin:0px;\">\r\n		<span style=\"font-family:\'Microsoft YaHei\';font-size:18px;color:#E56600;\"><strong>项目优势</strong></span>\r\n	</div>\r\n	<div style=\"padding:0px;margin:0px;\">\r\n		<span style=\"font-family:\'Microsoft YaHei\';font-size:16px;\">&nbsp; &nbsp; &nbsp;&nbsp;<span style=\"color:#337FE5;\">&nbsp;</span><span style=\"color:#003399;\">1.&nbsp;</span></span><span style=\"font-family:\'Microsoft YaHei\';font-size:16px;color:#003399;\">世界领先的开发商团队</span><span style=\"font-family:\'Microsoft YaHei\';font-size:16px;\"><span style=\"color:#003399;\">：</span>创立于1898年，Tishman Speyer铁狮门是享誉国际的房地产开发商、运营商和基金管理公司，目前在全球开发及管理380多个项目，在美国、欧洲、南美和亚洲的物业总值高达781亿美元；</span>\r\n	</div>\r\n	<div style=\"padding:0px;margin:0px;\">\r\n		<span style=\"font-family:\'Microsoft YaHei\';font-size:16px;\">&nbsp; &nbsp; &nbsp;&nbsp;<span style=\"color:#003399;\">&nbsp;</span><span style=\"color:#003399;\">2.&nbsp;</span></span><span style=\"font-family:\'Microsoft YaHei\';font-size:16px;color:#003399;\">业界成功经验最多的区域中心</span><span style=\"font-family:\'Microsoft YaHei\';font-size:16px;\"><span style=\"color:#003399;\">：</span>作为首家进入中国市场的区域中心，CanAm拥有28年投资移民业务经验，成功协助超过1700个家庭获得永久绿卡，1300多名投资者获得全额还款；</span>\r\n	</div>\r\n	<div style=\"padding:0px;margin:0px;\">\r\n		<span style=\"font-family:\'Microsoft YaHei\';font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp;<span style=\"color:#003399;\">3.&nbsp;</span></span><span style=\"font-family:\'Microsoft YaHei\';font-size:16px;color:#003399;\">项目地理位置优势</span><span style=\"font-family:\'Microsoft YaHei\';font-size:16px;\"><span style=\"color:#003399;\">：</span>纽约市政府全力支持长岛市重建开发计划，超过6300家公司进驻该区，拥有多所高等教育学校和大学，10分钟直达曼哈顿中城；</span>\r\n	</div>\r\n	<div style=\"padding:0px;margin:0px;\">\r\n		<span style=\"font-family:\'Microsoft YaHei\';font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp;<span style=\"color:#003399;\">4.&nbsp;</span></span><span style=\"font-family:\'Microsoft YaHei\';font-size:16px;color:#003399;\">资金安全担保</span><span style=\"font-family:\'Microsoft YaHei\';font-size:16px;\"><span style=\"color:#003399;\">：</span>EB-5资金只占21%，开发商股权现金投入超过50%，根据第三方评估报告项目价值为EB-5借款的6.5倍；</span>\r\n	</div>\r\n	<div style=\"padding:0px;margin:0px;\">\r\n		<span style=\"font-family:\'Microsoft YaHei\';font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp;<span style=\"color:#003399;\">5.&nbsp;</span></span><span style=\"font-family:\'Microsoft YaHei\';font-size:16px;color:#003399;\">还款担保</span><span style=\"font-family:\'Microsoft YaHei\';font-size:16px;\"><span style=\"color:#003399;\">：</span>铁狮门提供移民申请（I-526）及移民签证阶段不通过的还款担保;</span>\r\n	</div>\r\n	<div style=\"padding:0px;margin:0px;\">\r\n		<span style=\"font-family:\'Microsoft YaHei\';font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp;<span style=\"color:#003399;\">6.&nbsp;</span></span><span style=\"font-family:\'Microsoft YaHei\';font-size:16px;color:#003399;\">就业盈余充足</span><span style=\"font-family:\'Microsoft YaHei\';font-size:16px;\"><span style=\"color:#003399;\">：</span>超额满足就业要求，仅建筑就业即可满足EB-5所需就业；</span>\r\n	</div>\r\n	<div style=\"padding:0px;margin:0px;\">\r\n		<span style=\"font-family:\'Microsoft YaHei\';font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp;<span style=\"color:#003399;\">7.&nbsp;</span></span><span style=\"font-family:\'Microsoft YaHei\';font-size:16px;color:#003399;\">三重完工保障</span><span style=\"font-family:\'Microsoft YaHei\';font-size:16px;\"><span style=\"color:#003399;\">：</span>开发商与纽约市政府签署土地转让协议、银行贷款协议、及EB-5借款协议中皆包含完工保证</span>\r\n	</div>\r\n	<div style=\"padding:0px;margin:0px;\">\r\n		<span style=\"font-family:\'Microsoft YaHei\';font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp;<span style=\"color:#003399;\">8.&nbsp;</span></span><span style=\"font-family:\'Microsoft YaHei\';font-size:16px;color:#003399;\">退出机制简单</span><span style=\"font-family:\'Microsoft YaHei\';font-size:16px;\"><span style=\"color:#003399;\">：</span>通过再融资或出售大楼作为还款来源；</span>\r\n	</div>\r\n	<div style=\"padding:0px;margin:0px;\">\r\n		<span style=\"font-family:\'Microsoft YaHei\';font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp;<span style=\"color:#003399;\">9.&nbsp;</span></span><span style=\"font-family:\'Microsoft YaHei\';font-size:16px;color:#003399;\">项目旗舰租户已到位</span><span style=\"font-family:\'Microsoft YaHei\';font-size:16px;\"><span style=\"color:#003399;\">：</span>纽约知名高端百货已与开发商签订租赁合约。</span>\r\n	</div>\r\n	<p>\r\n		<br />\r\n	</p>\r\n</div>', '', '0');
INSERT INTO `xiaobing360_document_article` VALUES ('97', '0', '<p style=\"text-align:center;font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;font-size:16px;color:#3E3E3E;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:16px;\"><br />\r\n</span>\r\n</p>\r\n<p style=\"text-align:center;font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;font-size:16px;color:#3E3E3E;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:16px;\"><strong>且听总裁Tom Rosenfeld先生为您讲述老牌区域中心CanAm是怎样炼成的？</strong></span><span style=\"font-family:\'Microsoft YaHei\';font-size:16px;\"><strong></strong><strong></strong></span>\r\n</p>\r\n<p style=\"text-align:center;font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;font-size:16px;color:#3E3E3E;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:16px;\"><br />\r\n</span>\r\n</p>\r\n<div style=\"text-align:center;\">\r\n	<img src=\"https://mmbiz.qlogo.cn/mmbiz_jpg/3dVZQ7f409UWwq72hW7TndqlQa30uiahVIdN9JN6PZZfibiaVIGQHWOlKHykBaWiagMD6bxMoMBpYjpCax5o8rfCbg/0?wx_fmt=jpeg\" class=\"\" style=\"height:auto !important;\" /><span style=\"line-height:1.5;\"></span>\r\n</div>\r\n<span style=\"font-family:\'Microsoft YaHei\';font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp;做为老牌区域中心，CanAm基金公司</span><span style=\"line-height:1.6;\"><span style=\"font-family:\'Microsoft YaHei\';font-size:16px;\">拥有</span><strong><span style=\"line-height:1.6;font-size:16px;color:#DEB451;font-family:\'Microsoft YaHei\';\">30</span></strong><span style=\"font-family:\'Microsoft YaHei\';font-size:16px;\">年从事移民投资的历史，成立</span><span style=\"line-height:1.6;color:#DEB451;font-size:16px;font-family:\'Microsoft YaHei\';\"><strong>6</strong></span><span style=\"font-family:\'Microsoft YaHei\';font-size:16px;\">个区域中心，募集了超过</span><span style=\"line-height:1.6;color:#DEB451;font-size:16px;font-family:\'Microsoft YaHei\';\"><strong>20</strong></span><span style=\"font-family:\'Microsoft YaHei\';font-size:16px;\">亿美金的EB-5资金，返还</span><strong><span style=\"line-height:1.6;color:#DEB451;font-size:16px;font-family:\'Microsoft YaHei\';\">100%</span></strong><span style=\"font-family:\'Microsoft YaHei\';font-size:16px;\">本金给</span><strong><span style=\"line-height:1.6;font-size:16px;color:#DEB451;font-family:\'Microsoft YaHei\';\">1333</span></strong><span style=\"font-family:\'Microsoft YaHei\';font-size:16px;\">个投资者家庭，超过</span><span style=\"line-height:1.6;font-size:16px;font-family:\'Microsoft YaHei\';\"><strong><span style=\"line-height:1.6;color:#DEB451;font-family:\'Microsoft YaHei\';font-size:16px;\">50%</span></strong></span><span style=\"font-family:\'Microsoft YaHei\';font-size:16px;\">的中国永久绿卡持有者都来自CanAm，超过</span><span style=\"line-height:1.6;color:#DEB451;font-size:16px;font-family:\'Microsoft YaHei\';\"><strong>3100名</strong></span><span style=\"font-family:\'Microsoft YaHei\';font-size:16px;\">投资家庭已取得有条件绿卡。</span></span><br />\r\n<p style=\"font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;font-size:16px;\">\r\n	<br />\r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:16px;\">&nbsp; &nbsp; &nbsp; CanAm拥有如此辉煌的成就，绝非一朝一夕所能铸就的，且听CanAm总裁Tom Rosenfeld先生亲述，是什么造就了CanAm今日的辉煌？</span>\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<iframe class=\"video_iframe\" height=\"375\" width=\"500\" frameborder=\"0\" src=\"https://v.qq.com/iframe/preview.html?vid=c0317ndm3n2&amp;width=500&amp;height=375&amp;auto=0\" style=\"width:556px;\">\r\n	</iframe>\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<span style=\"color:#FF6827;font-family:\'Microsoft YaHei\';font-size:16px;\"><strong>CanAm辉煌历史</strong></span>\r\n<p>\r\n	<br />\r\n</p>\r\n<img border=\"0\" class=\"lazy\" src=\"https://mmbiz.qlogo.cn/mmbiz_png/3dVZQ7f409UWwq72hW7TndqlQa30uiahVChyCzialewnAmqpwqViaju1keC8ic7ia5Lx6icLSNjmdulNhP4zceGBar6Q/0?wx_fmt=png\" height=\"\" title=\"5.png\" width=\"100%\" style=\"height:auto !important;width:422.797px;\" />\r\n<p class=\"135brush\">\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<img border=\"0\" class=\"lazy\" src=\"https://mmbiz.qlogo.cn/mmbiz_png/3dVZQ7f409UWwq72hW7TndqlQa30uiahV0ktGVQQJVxNSR86MQNassum7xQokctVUM1318gUxvMRAjoh9bFSlew/0?wx_fmt=png\" height=\"\" title=\"2.png\" width=\"100%\" style=\"height:auto !important;width:422.797px;\" />\r\n<p class=\"135brush\">\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<img border=\"0\" class=\"lazy\" src=\"https://mmbiz.qlogo.cn/mmbiz_jpg/3dVZQ7f409UWwq72hW7TndqlQa30uiahV8IjtHHg4bA8yMWcPN79xqrLibS3Q5ISRWYojkdY5fxvIk6tN6ib8vWkw/0?wx_fmt=jpeg\" height=\"\" title=\"1.jpg\" width=\"100%\" style=\"height:auto !important;width:422.797px;\" />\r\n<p class=\"135brush\">\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<img border=\"0\" class=\"lazy\" src=\"https://mmbiz.qlogo.cn/mmbiz_png/3dVZQ7f409UWwq72hW7TndqlQa30uiahVqwbqCcSAHVmIoE3eVwOC6LMujB2oxPydZ3aCQcBEHhe34cuNAOUADg/0?wx_fmt=png\" height=\"\" title=\"3.png\" width=\"100%\" style=\"height:auto !important;width:422.797px;\" />\r\n<p class=\"135brush\">\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<img src=\"https://mmbiz.qlogo.cn/mmbiz_png/3dVZQ7f409UWwq72hW7TndqlQa30uiahV7zvzPIRu2eUUib4a1CDvTAffl3gsgchaLUtKnibVxEy8rSGg6aZjgHIQ/0?wx_fmt=png\" style=\"height:auto !important;\" /><br />\r\n<p class=\"135brush\">\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<img border=\"0\" class=\"lazy\" src=\"https://mmbiz.qlogo.cn/mmbiz_png/3dVZQ7f409UWwq72hW7TndqlQa30uiahVB43Yaia9zYBHztL8mg9KbpRoNBApYEicI6sXyPbXuIwKICNgAYU0lmyA/0?wx_fmt=png\" height=\"\" title=\"无标题.png\" width=\"100%\" style=\"height:auto !important;width:422.797px;\" />\r\n<p class=\"135brush\">\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<span style=\"color:#FF6827;font-family:\'Microsoft YaHei\';font-size:16px;\"><strong>关于CanAm</strong></span>\r\n<p>\r\n	<br />\r\n</p>\r\n<p style=\"font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;font-size:16px;\">\r\n	<br />\r\n</p>\r\n<img src=\"https://mmbiz.qlogo.cn/mmbiz_png/3dVZQ7f409UWwq72hW7TndqlQa30uiahVAm9nN6ygrevoeZh5SrAyBJicuxWHDTM3m1vfj7USmav2TLyzkcqRt0g/0?wx_fmt=png\" class=\"\" style=\"height:auto !important;\" /><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';font-size:16px;\">美国美加企业(CanAm Enterprises)，EB-5区域中心计划领导品牌，美国最顶级的EB-5投资移民公司之一，在促进美国和加拿大与移民有关的投资方面具有近30年的经验，已帮助众多移民家庭成功投资而取得海外居留资格。</span><br />\r\n<img src=\"https://mmbiz.qlogo.cn/mmbiz_png/3dVZQ7f409UWwq72hW7TndqlQa30uiahVOGehVZPyvrGQUyEOEykMHX2T2EPicLCawMnYsEgFM3fibqO267YQmffg/0?wx_fmt=gif\" class=\"\" style=\"height:9.51563px;width:25.5938px;\" /><span style=\"font-family:\'Microsoft YaHei\';font-size:16px;\">1987年成立金融管理顾问公司，</span><strong><span style=\"color:#DEB451;font-family:\'Microsoft YaHei\';font-size:16px;\">30</span></strong><span style=\"font-family:\'Microsoft YaHei\';font-size:16px;\">年以上投资移民业务经验。与政府单位合作，慎选优质大型企业，以</span><strong><span style=\"color:#DEB451;font-family:\'Microsoft YaHei\';font-size:16px;\">担保借贷方式</span></strong><span style=\"font-family:\'Microsoft YaHei\';font-size:16px;\">取代投资。</span><img src=\"https://mmbiz.qlogo.cn/mmbiz_png/3dVZQ7f409UWwq72hW7TndqlQa30uiahVOGehVZPyvrGQUyEOEykMHX2T2EPicLCawMnYsEgFM3fibqO267YQmffg/0?wx_fmt=gif\" class=\"\" style=\"height:9.51563px;width:25.5938px;\" /><span style=\"font-family:\'Microsoft YaHei\';font-size:16px;\">现有</span><strong><span style=\"color:#DEB451;font-family:\'Microsoft YaHei\';font-size:16px;\">46</span></strong><span style=\"font-family:\'Microsoft YaHei\';font-size:16px;\">个已结案投资移民项目，其中</span><span style=\"color:#DEB451;font-family:\'Microsoft YaHei\';font-size:16px;\"><strong>29</strong></span><span style=\"font-family:\'Microsoft YaHei\';font-size:16px;\">个项目已期满顺利取回原始投资资金，并还款100%本金给</span><strong><span style=\"color:#DEB451;font-family:\'Microsoft YaHei\';font-size:16px;\">1333</span></strong><span style=\"font-family:\'Microsoft YaHei\';font-size:16px;\">名投资家庭。至今已帮助约</span><span style=\"color:#DEB451;font-family:\'Microsoft YaHei\';font-size:16px;\"><strong>1800</strong></span><span style=\"font-family:\'Microsoft YaHei\';font-size:16px;\">个投资家庭获得永久绿卡，为业界成功经验最多之基金公司。</span><img src=\"https://mmbiz.qlogo.cn/mmbiz_png/3dVZQ7f409UWwq72hW7TndqlQa30uiahVOGehVZPyvrGQUyEOEykMHX2T2EPicLCawMnYsEgFM3fibqO267YQmffg/0?wx_fmt=gif\" class=\"\" style=\"height:9.51563px;width:25.5938px;\" /><span style=\"font-family:\'Microsoft YaHei\';font-size:16px;\">EB--5 第一阶段：I--526临时绿卡，至今已有</span><span style=\"color:#DEB451;font-family:\'Microsoft YaHei\';font-size:16px;\"><strong>3100</strong></span><span style=\"font-family:\'Microsoft YaHei\';font-size:16px;\">名投资家庭获得临时绿卡。EB--5最重要环节：I--829解除条件，至今已有</span><strong><span style=\"color:#DEB451;font-family:\'Microsoft YaHei\';font-size:16px;\">1800</span></strong><span style=\"font-family:\'Microsoft YaHei\';font-size:16px;\">名投资家庭获得永久绿卡，为业界成功经验最多之基金公司。</span><img src=\"https://mmbiz.qlogo.cn/mmbiz_png/3dVZQ7f409UWwq72hW7TndqlQa30uiahVOGehVZPyvrGQUyEOEykMHX2T2EPicLCawMnYsEgFM3fibqO267YQmffg/0?wx_fmt=gif\" class=\"\" style=\"height:9.51563px;width:25.5938px;\" /><span style=\"font-family:\'Microsoft YaHei\';font-size:16px;\">在中国，超过</span><span style=\"color:#DEB451;font-family:\'Microsoft YaHei\';font-size:16px;\"><strong>半数以上</strong></span><span style=\"font-family:\'Microsoft YaHei\';font-size:16px;\">的I-829批准都来自CanAm！全美首家创办FINRA注册经纪交易商（CanAm Investor Services, LLC）的EB-5公司。</span>\r\n<p style=\"font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;font-size:16px;\">\r\n	<br />\r\n</p>\r\n<p style=\"font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;font-size:16px;\">\r\n	<br />\r\n</p>', '', '0');
INSERT INTO `xiaobing360_document_article` VALUES ('119', '0', '<p style=\"text-align:center;\">\r\n	<span style=\"font-size:18px;font-family:\'Microsoft YaHei\';\"><strong><br />\r\n</strong></span>\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"font-size:18px;font-family:\'Microsoft YaHei\';\"><strong>【澳洲置业】墨尔本OPERA-与澳洲城市的亲密衔接</strong></span>\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"font-size:18px;font-family:\'Microsoft YaHei\';\"><strong><br />\r\n</strong></span>\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/Websites/zhongyu/Uploads/Editor/2016-09-06/57ce27c9a8aa9.png\" alt=\"\" />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/Websites/zhongyu/Uploads/Editor/2016-09-06/57ce2911f2a4d.png\" alt=\"\" />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-align:left;\">\r\n	<p style=\"text-align:center;\">\r\n		<span style=\"line-height:1.5;\"></span><img src=\"/Websites/zhongyu/Uploads/Editor/2016-09-06/57ce29d82a1a5.jpg\" alt=\"\" />\r\n	</p>\r\n	<p style=\"text-align:center;\">\r\n		<br />\r\n	</p>\r\n	<p class=\"Default\" style=\"text-align:center;\">\r\n		<span style=\"font-family:\'Microsoft YaHei\';font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp;Opera</span><span style=\"font-family:\'Microsoft YaHei\';font-size:16px;\">项目位于墨尔本圣柯达路繁华商业街，距离CBD仅<span style=\"color:#E56600;\">2.9</span>Km，可俯瞰皇家植物园，绿色天然氧吧环绕，交通便利，火车、有轨电车和巴士<span style=\"color:#E56600;\">四通八达</span>。</span>\r\n	</p>\r\n	<p class=\"Default\" style=\"text-align:center;\">\r\n		<span style=\"font-family:\'Microsoft YaHei\';font-size:16px;\"><img src=\"/Websites/zhongyu/Uploads/Editor/2016-09-06/57ce2a250da3e.png\" alt=\"\" /></span>\r\n	</p>\r\n	<p class=\"Default\">\r\n		<span style=\"font-family:\'Microsoft YaHei\';font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp;由一流的Bates Smart建筑师设计。这套漂亮的公寓是St Kilda路上的靓丽风景线，采用精美的玻璃外观，弧形和曲形完美结合，直耸天际，位于墨尔本先前的<span style=\"color:#E56600;\">贵族区</span>，凸显了此林荫大道的历史特色。墨尔本最恢弘的林荫道也是通往CBD的门户之地，周边有<span style=\"color:#E56600;\">茂</span><span style=\"color:#E56600;\">盛的公共绿地区</span>、<span style=\"color:#E56600;\">一流学校</span>和市区<span style=\"color:#E56600;\">著名的艺术区</span>。此公寓位于全世界最宜居的市区，<span style=\"color:#E56600;\">位置便利</span>，几分钟便可达高档零售区、一流的餐厅、人气咖啡厅和巷道文化区。</span>\r\n	</p>\r\n	<p class=\"Default\">\r\n		<span style=\"font-family:\'Microsoft YaHei\';font-size:16px;\"><br />\r\n</span>\r\n	</p>\r\n</p>', '', '0');
